import {useState} from "react";
import styled, {css} from "styled-components";
import {MdAdd} from 'react-icons/md';

const CircleButton = styled.div`
  background: #38d9a9;
  &:hover {
    background: #63e6be;
  }
  &:active {
    background: #20c997;
  }

  z-index: 5;
  cursor: pointer;
  width: 80px;
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: center;

  position: absolute;
  left: 50%;
  bottom: 0px;
  transform: translate(-50%, 50%);

  font-size: 60px;
  color: white;
  border-radius: 40px;

  border: none;
  outline: none;


`


function TodoCreate() {
    const [open, setOpen] = useState(false);
    const onToggle = () => {
        setOpen(!open)
    }
    return(
        <CircleButton open={open} onClick={onToggle}>
            <MdAdd/>
        </CircleButton>
    )
}

export default TodoCreate;
